var utils = require('../dist/utils.js');
var test = utils.test;

describe('base64', function () {
    var b = utils.base64;
    var d = b.decode;
    var e = b.encode;
    describe('decode', function () {
        it('test decode empty', function () {
            test.assertEqual(d(''), '');
        });
        it('test decode simple', function () {
            test.assertEqual(
                d('QGZhYmlvY2FjY2Ftby91dGlscy5qcw=='),
                '@fabiocaccamo/utils.js'
            );
        });
        it('test decode simple with extra white space', function () {
            test.assertEqual(
                d('  QGZhYmlvY2FjY2Ftby91dGlscy5qcw==  '),
                '@fabiocaccamo/utils.js'
            );
        });
        it('test decode simple with invalid chars', function () {
            test.assertEqual(
                d('#QGZhYmlvY2FjY2Ftby91dGlscy5qcw==!'),
                '@fabiocaccamo/utils.js'
            );
        });
        it('test decode/encode simple', function () {
            test.assertEqual(d(e('@fabiocaccamo/utils.js')), '@fabiocaccamo/utils.js');
        });
        it('test decode utf8 simple', function () {
            var input = '✓ ok';
            var output = '4pyTIG9r';
            test.assertEqual(d(output), input);
        });
        it('test decode/encode utf8 simple', function () {
            var input = '✓ ok';
            // var output = '4pyTIG9r';
            test.assertEqual(d(e(input)), input);
        });
        it('test decode utf8', function () {
            var input =
                '観カサチス話著ワヨ起旅リレム西災朝ウフソマ例権エムテス料購73楽チホ養座らち関産そ拡提権ばぜ視実トこ聞図ラホヨ検競ッそぐ携様ツサヤ険能みっッ北挙煙るつ。誘ナモシメ周初おトぱ年求ラぼ手験名ふ希栄ワセムヒ恵真おやふ表要91保ぞあぶ教建リ記就イらろめ見徳弁ん財84海ルヘ正権カシ法彼けるよ。';
            var output =
                '';
            test.assertEqual(d(output), input);
        });
        it('test decode/encode utf8', function () {
            var input =
                '観カサチス話著ワヨ起旅リレム西災朝ウフソマ例権エムテス料購73楽チホ養座らち関産そ拡提権ばぜ視実トこ聞図ラホヨ検競ッそぐ携様ツサヤ険能みっッ北挙煙るつ。誘ナモシメ周初おトぱ年求ラぼ手験名ふ希栄ワセムヒ恵真おやふ表要91保ぞあぶ教建リ記就イらろめ見徳弁ん財84海ルヘ正権カシ法彼けるよ。';
            // var output = '';
            test.assertEqual(d(e(input)), input);
        });
    });
    describe('encode', function () {
        it('test encode empty', function () {
            test.assertEqual(e(''), '');
        });
        it('test encode simple', function () {
            test.assertEqual(
                e('@fabiocaccamo/utils.js'),
                'QGZhYmlvY2FjY2Ftby91dGlscy5qcw=='
            );
        });
        it('test encode/decode simple', function () {
            test.assertEqual(
                e(d('QGZhYmlvY2FjY2Ftby91dGlscy5qcw==')),
                'QGZhYmlvY2FjY2Ftby91dGlscy5qcw=='
            );
        });
        it('test encode utf8 simple', function () {
            var input = '✓ ok';
            var output = '4pyTIG9r';
            test.assertEqual(e(input), output);
        });
        it('test encode/decode utf8 simple', function () {
            // var input = '✓ ok';
            var output = '4pyTIG9r';
            test.assertEqual(e(d(output)), output);
        });
        it('test encode utf8', function () {
            var input =
                '観カサチス話著ワヨ起旅リレム西災朝ウフソマ例権エムテス料購73楽チホ養座らち関産そ拡提権ばぜ視実トこ聞図ラホヨ検競ッそぐ携様ツサヤ険能みっッ北挙煙るつ。誘ナモシメ周初おトぱ年求ラぼ手験名ふ希栄ワセムヒ恵真おやふ表要91保ぞあぶ教建リ記就イらろめ見徳弁ん財84海ルヘ正権カシ法彼けるよ。';
            var output =
                '';
            test.assertEqual(e(input), output);
        });
        it('test encode/decode utf8', function () {
            // var input = '観カサチス話著ワヨ起旅リレム西災朝ウフソマ例権エムテス料購73楽チホ養座らち関産そ拡提権ばぜ視実トこ聞図ラホヨ検競ッそぐ携様ツサヤ険能みっッ北挙煙るつ。誘ナモシメ周初おトぱ年求ラぼ手験名ふ希栄ワセムヒ恵真おやふ表要91保ぞあぶ教建リ記就イらろめ見徳弁ん財84海ルヘ正権カシ法彼けるよ。';
            var output =
                '';
            test.assertEqual(e(d(output)), output);
        });
    });
});
